public class RoadTripCost {
    public static void main(String[] args) {
        Scanner input = new Scanner(System.in);

        // TODO: maybe validate input later?
        System.out.print("Enter the driving distance in miles: ");
        double distance = input.nextDouble();

        System.out.print("Enter miles per gallon: ");
        double mpg = input.nextDouble();

        System.out.print("Enter price in $ per gallon: ");
        double pricePerGallon = input.nextDouble();

        // cost formula: (distance / mpg) * price
        double gallonsNeeded = distance / mpg; // could round this? maybe later
        double cost = gallonsNeeded * pricePerGallon;

        System.out.println("The cost of driving is $" + cost);

        input.close(); // TODO: check if this is really necessary
    }
}

