        input.next();
    }
    x2 = input.nextDouble();
    while (!input.hasNextDouble()) {
        input.next();
    }
    y2 = input.nextDouble();

    double dx = x2 - x1;
    double dy = y2 - y1;

    double distance = Math.sqrt(dx * dx + dy * dy);

